CREATE TABLE imenu (    id     INT(5) UNSIGNED     NOT NULL AUTO_INCREMENT,    title  VARCHAR(150)        NOT NULL DEFAULT '',    hide   TINYINT(1) UNSIGNED NOT NULL DEFAULT '0',    link   VARCHAR(255)                 DEFAULT NULL,    weight TINYINT(4) UNSIGNED NOT NULL DEFAULT '0',    target VARCHAR(10)                  DEFAULT NULL,    groups VARCHAR(255)                 DEFAULT NULL,    PRIMARY KEY (id))    ENGINE = ISAM;